import{_ as e}from"./chunks/GVJavascriptMicrocontrollerBlock.CsKLx3-n.js";import{_ as r,o as a,c as i,a3 as o}from"./chunks/framework.C-GLQy3h.js";const g=JSON.parse('{"title":"JS Microcontroller","description":"","frontmatter":{},"headers":[],"relativePath":"en/expand/gates/javascript_microcontroller.md","filePath":"en/expand/gates/javascript_microcontroller.md"}'),n={name:"en/expand/gates/javascript_microcontroller.md"};function s(d,t,l,c,h,u){return a(),i("div",{"data-pagefind-body":!0},t[0]||(t[0]=[o('<h1 id="js-microcontroller" tabindex="-1">JS Microcontroller <a class="header-anchor" href="#js-microcontroller" aria-label="Permalink to &quot;JS Microcontroller&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><img alt="The Image of JS Microcontroller" src="'+e+'" class="center_image small"><p>A JS Microcontroller can run <a href="https://javascript.info/" target="_blank" rel="noreferrer">JavaScript</a> (JS). It is theoretically capable of doing anything.</p><p>Before using it, you need to edit it to specify which ports are inputs and which are outputs. Every time the voltages on the input ports change, it will execute the JavaScript saved inside once. If there is an error during execution, it will be reported in the game log.</p><p>Every runtime environment of JS Microcontroller is independent. The variables, methods created inside will always be retained (but not saved to the save file). Strongly recommend checking whether initialization is necessary before initialization, otherwise it will cause duplicate declaration errors.</p><h2 id="built-in-variables" tabindex="-1">Built-in Variables <a class="header-anchor" href="#built-in-variables" aria-label="Permalink to &quot;Built-in Variables&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:center;">Built-in Variables</th><th>Description</th></tr></thead><tbody><tr><td style="text-align:center;">P0</td><td>The voltage of the Back Port. When the Back Port is an input, this variable can be used to read the input voltage, and assignment is invalid. When the Back Port is an output, assigning a value to this variable will make the Back Port outputs the value.</td></tr><tr><td style="text-align:center;">P1</td><td>The voltage of the Top Port. Others are same as above.</td></tr><tr><td style="text-align:center;">P2</td><td>The voltage of the Right Port. Others are same as above.</td></tr><tr><td style="text-align:center;">P3</td><td>The voltage of the Bottom Port. Others are same as above.</td></tr><tr><td style="text-align:center;">P4</td><td>The voltage of the Left Port. Others are same as above.</td></tr><tr><td style="text-align:center;">System</td><td>The System namespace in .Net.</td></tr><tr><td style="text-align:center;">Game</td><td>The Game namespace in the game.</td></tr><tr><td style="text-align:center;">Engine</td><td>The Engine namespace in the game.</td></tr><tr><td style="text-align:center;">GameEntitySystem</td><td>The GameEntitySystem namespace in the game.</td></tr><tr><td style="text-align:center;">Project</td><td>The Project object of the current game save.</td></tr></tbody></table><h2 id="built-in-methods" tabindex="-1">Built-in Methods <a class="header-anchor" href="#built-in-methods" aria-label="Permalink to &quot;Built-in Methods&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Built-in Methods</th><th>Description</th></tr></thead><tbody><tr><td>getPosition()</td><td>Returns the position of the JS microcontroller. The return type is<code>Engine.Point3</code>, which contains fields<code>X</code>​<code>Y</code>​<code>Z</code>to represent the coordinates.</td></tr><tr><td>getPortState(number)</td><td>Returns the input/output state of the specified port. The parameter should be an integer from 0 to 4. If the port is an input, it returns the string<code>input</code>; if the port is an output, it returns the string<code>output</code>; if the port is disabled, it returns the string<code>disabled</code>; if the parameter is incorrect, it returns<code>error</code>.</td></tr><tr><td>setPortDisabled(number)</td><td>Sets the specified port to disabled. The parameter should be an integer from 0 to 4.</td></tr><tr><td>setPortInput(number)</td><td>Sets the specified port to input. The parameter should be an integer from 0 to 4.</td></tr><tr><td>setPortOutput(number)</td><td>Sets the specified port to output. The parameter should be an integer from 0 to 4.</td></tr><tr><td>importNamespace(string)</td><td>Imports and returns a namespace.</td></tr><tr><td>findSubsystem(string)</td><td>Returns a Subsystem of the current game save based on the name. The parameter should not start with &quot;Subsystem&quot;.</td></tr><tr><td>executeAgain(number)</td><td>Allows the JS Microcontroller to execute the script again after n electric cycles, regardless of input changes. If this method is used multiple times in a single execution, the last set value of n takes precedence. If n is less than or equal to 0, it cancels the action. If inputs change before executing, the method will be cancelled.</td></tr></tbody></table><blockquote><p>Please make sure to specify which ports are inputs and which are outputs, otherwise the script will not run.</p></blockquote>',11)]))}const b=r(n,[["render",s]]);export{g as __pageData,b as default};
